import React, { useCallback, useEffect, useState } from 'react';
import { AiTwotoneHome } from 'react-icons/ai';
import { BsGearFill } from 'react-icons/bs';
import { FiUser } from 'react-icons/fi';
import { MdImportExport } from 'react-icons/md';
import { RiMenuAddLine, RiPieChartFill } from 'react-icons/ri';
import { Link, useLocation } from 'react-router-dom';
import { MenuBar } from './styles';

interface MenuProps {
  style: Object;
  isVisibleMenu: boolean;
}

interface OptionsPathName {
  option: 'dashboard' | 'profile' | 'default' | 'registration';
}

const Menu: React.FC<MenuProps> = ({ style, isVisibleMenu }) => {
  const { pathname } = useLocation();

  const getValidationPathName = useCallback((): OptionsPathName => {
    let pathnamePage = pathname.replace('/', '');
    if (
      pathnamePage !== 'dashboard' &&
      pathname !== 'registration' &&
      pathnamePage !== 'profile'
    ) {
      pathnamePage = 'default';
    }
    return {
      option: pathnamePage,
    } as OptionsPathName;
  }, [pathname]);

  const validationPathName = useCallback(() => {
    const pathnameOptions = {
      default: 0,
      dashboard: 2,
      profile: 6,
      registration: 5,
    };
    const options = getValidationPathName();
    return pathnameOptions[options.option];
  }, [getValidationPathName]);

  const [childMenu, setChildMenu] = useState<number>(() => {
    return validationPathName();
  });

  useEffect(() => {
    getValidationPathName();
  }, [getValidationPathName]);

  if (!isVisibleMenu) {
    return <> </>;
  }
  return (
    <MenuBar child={childMenu} style={style}>
      <nav>
        <ul>
          <li>
            <a href=" ">dsa</a>
          </li>
          <li>
            <Link
              to="/dashboard"
              onClick={() => {
                setChildMenu(2);
              }}
            >
              <AiTwotoneHome size={18} color="#ffffff" />
              Dashboard
            </Link>
          </li>
          <li>
            <a href=" ">
              <RiPieChartFill size={18} color="#ffffff" />
              Resultados
            </a>
          </li>
          <li>
            <a href=" ">
              <MdImportExport size={18} color="#ffffff" />
              Importações
            </a>
          </li>
          <li>
            <Link
              to="/registration"
              onClick={() => {
                setChildMenu(5);
              }}
            >
              <RiMenuAddLine size={18} color="#ffffff" />
              Cadastros
            </Link>
          </li>
          <li>
            <Link
              to="/profile"
              onClick={() => {
                setChildMenu(6);
              }}
            >
              <FiUser size={18} color="#ffffff" />
              Perfil
            </Link>
          </li>
          <li>
            <a href=" ">
              <BsGearFill size={18} color="#ffffff" />
              Configurações
            </a>
          </li>
          <li>
            <a href=" ">Teste</a>
          </li>
        </ul>
      </nav>
      <footer>
        <p>Análise de Relatórios</p>
      </footer>
    </MenuBar>
  );
};

export default Menu;
