import styled from 'styled-components';

export const TableFilter = styled.div`
  display: flex;

  div {
    display: flex;
    align-items: center;
    justify-content: center;
    border: 0.2rem solid #2691f0;
    height: 1.5rem;
    flex: 1;

    svg {
      margin: 0 0.1.5rem;
    }

    input {
      border: none;
      padding: 0 0.4rem;
      height: 1.5rem;
      width: 100%;
      color: #969cb3;
      background: transparent;
    }
  }
`;

export const LimitePerPage = styled.select`
  margin-bottom: 0.5rem;
  height: 1.8rem;
  border-color: #2691f0;
  color: #969cb3;
  background-color: #fff;
  border-radius: 0.2rem;
`;

export const TableStyle = styled.table`
  width: 100%;
  border-collapse: collapse;
  thead {
    background: linear-gradient(64.42deg, #35cfff 21.97%, #2691f0 99.92%);
  }
  th {
    color: #fff;
    font-weight: normal;
    padding: 0.8rem 2rem;
    text-align: left;
    font-size: 1.3rem;
    line-height: 1rem;
    font-family: Roboto;

    text-align: center;
  }
  td {
    padding: 0.6rem 2rem;
    border: 0;
    background: #fff;
    font-size: 1rem;
    font-weight: normal;
    color: #969cb3;
  }
  @media (max-width: 800px) {
    td {
      padding: 0.2rem 1.5rem;
    }

    th {
      padding: 0.4rem 1.5rem;
    }
  }
`;

export const Pagination = styled.div`
  button {
    background: transparent;
    border: 0.05rem solid #2691f0;
    height: 2rem;
    width: 2rem;

    display: flex;
    justify-content: center;
    align-items: center;

    margin-right: 0.1rem;
    color: #969cb3;

    svg {
      color: #969cb3;
    }
    &:hover {
      background-color: #ccc;
    }
  }
  div {
    margin-top: 0.5rem;
    display: flex;
  }
`;
