import styled from 'styled-components';

export const Content = styled.main`
  width: 100%;
  max-width: 70rem;
`;

export const Title = styled.div`
  background-color: #fff;

  height: 4rem;
  border-radius: 0.8rem;

  padding: 1rem;
  margin: 2rem 0;

  display: flex;
  flex-direction: column;
  justify-content: center;

  strong {
    font-family: Roboto;
    font-weight: 800;
    font-size: 1.8rem;
    line-height: 2rem;

    color: #2691f0;
  }
  p {
    font-family: Roboto;
    font-weight: 800;
    font-size: 1.1rem;
    line-height: 1.4rem;

    color: #888888;
  }
`;
export const StoreProducts = styled.div`
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
`;
export const Product = styled.div`
  max-width: 20rem;
  width: 100%;

  padding: 1rem;
  background-color: #fff;
  border-radius: 0.8rem;

  img {
    width: 100%;
    height: 120%;
  }
`;

export const Price = styled.div``;
