import React, { createContext, useCallback, useContext, useState } from 'react';
import Alert from '../components/Alert';

interface ICreateAlertProps {
  addAlert(data: IMessage): void;
  alertConfirm(confirmation: boolean): void;
  confirmation?: boolean;
}
interface IMessage {
  message: string;
  type?: 'error' | 'confirmation' | 'alert' | 'success';
  returnMessage?(): boolean;
}

const AlertContext = createContext<ICreateAlertProps>({} as ICreateAlertProps);

export const AlertMessage: React.FC = ({ children }) => {
  const [alert, setAlert] = useState<IMessage>({} as IMessage);
  const [confirmation, setConfirmation] = useState<boolean>(true);

  const addAlert = useCallback(({ message, type }: IMessage) => {
    setAlert({
      message,
      type,
      returnMessage: handleConfirmation(),
    });
  }, []);

  const alertConfirm = useCallback((response: boolean) => {
    setConfirmation(response);
    setAlert({} as IMessage);
  }, []);

  const handleConfirmation = useCallback((response: boolean): boolean => {
    setConfirmation(response);
    return response;
  }, []);

  return (
    <AlertContext.Provider value={{ addAlert, alertConfirm, confirmation }}>
      <Alert
        message={alert.message}
        type={alert.type}
        returnMessage={handleConfirmation}
      >
        {children}
      </Alert>
    </AlertContext.Provider>
  );
};

export function useAlert() {
  const context = useContext(AlertContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider.');
  }
  return context;
}
