import { shade } from 'polished';
import styled from 'styled-components';

export const Content = styled.main`
  display: flex;
  justify-content: center;
  align-items: center;

  flex-direction: column;

  form {
    width: 30rem;
    > div:first-child {
      width: calc(50% - 0.6rem);
    }
  }

  table {
    tr {
      background-color: #fff;
    }
  }
`;

export const NewPassword = styled.div`
  display: flex;
  div + div {
    margin: 0 0 0 1rem;
  }
`;

export const ProfileAvatar = styled.div`
  margin: 2rem 0;
  position: relative;
  input {
    display: none;
  }
  img {
    height: 186px;
    width: 186px;
    border-radius: 50%;
  }
  > label {
    position: absolute;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;

    background: #2691f0;

    height: 50px;
    width: 50px;

    right: 0;
    top: 140px;

    border-radius: 50%;
    :hover {
      background: ${shade('0.2', '#2691f0')};
    }
  }
`;
