import { shade } from 'polished';
import styled from 'styled-components';

export const MenuBar = styled.aside`
  position: fixed;
  width: 12rem;
  background-color: #2691f0;
  height: 100vh;
  z-index: 5;

  nav ul {
    margin: 6rem 0 0 2rem;
  }

  nav ul li {
    height: 5rem;
    width: 6rem;
    border-radius: 0.8rem;
    display: flex;
    align-items: center;
    justify-content: center;

    transition: 0.2s;
    :hover {
      background: ${shade('0.1', '#2691f0')};
    }

    a {
      cursor: pointer !important;
      display: flex;
      flex-direction: column;
      justify-content: flex-start;
      align-items: center;

      text-decoration: none;

      svg {
        transition: 0.2s;
      }
      label {
        margin-top: 0.4rem;

        font-size: 0.9rem;
        color: #fff;
      }
      & svg:hover {
        color: ${shade('0.2', '#f2f2f2')};
        height: 2.6rem;
        width: 2.6rem;
      }
    }
  }
  .ative {
    background: #eeeeee;
    :hover {
      background: #eeeeee;
    }
    a svg {
      color: #2691f0 !important;
      &:hover {
        color: ${shade('0.2', '#2691f0')};
      }
    }
  }
`;
