import styled from 'styled-components';

export const Content = styled.main`
  display: flex;
  justify-content: center;
  align-items: center;

  flex-direction: column;
`;

export const ProfileAvatar = styled.div`
  margin: 2rem 0;
  position: relative;
  input {
    display: none;
  }
  img {
    height: 186px;
    width: 186px;
    border-radius: 50%;
  }
  > label {
    position: absolute;
    display: flex;
    align-items: center;
    justify-content: center;

    background: #2691f0;

    height: 50px;
    width: 50px;
    border-radius: 50%;

    margin-left: auto;
  }
`;
