import { FormHandles } from '@unform/core';
import React, { useCallback, useMemo, useRef } from 'react';
import { AiOutlineUsergroupAdd } from 'react-icons/ai';
import { FiMail, FiUser } from 'react-icons/fi';
import { ImProfile } from 'react-icons/im';
import { Form } from '@unform/web';
import Button from '../../../../../components/Button';
import Input from '../../../../../components/Input';
import Modal from '../../../../../components/Modal';
import Select from '../../../../../components/Select';

interface IModalProps {
  isOpen: boolean;
  setIsOpen: () => void;
  handleUpdateUser: () => void;
}

export const ModalEditUser: React.FC<IModalProps> = ({
  setIsOpen,
  isOpen,
  children,
  handleUpdateUser,
}) => {
  const formRef = useRef<FormHandles>(null);

  const groupUsers = useMemo(() => {
    return [
      {
        value: '1',
        description: 'Belo Horizonte',
      },
      {
        value: '2',
        description: 'Distrito Federal',
      },
      {
        value: '3',
        description: 'Brasilia',
      },
    ];
  }, []);

  const profilesUser = useMemo(() => {
    return [
      {
        value: '1',
        description: 'Administrador',
      },
      {
        value: '2',
        description: 'Usuário',
      },
    ];
  }, []);

  const handleSubmit = useCallback(() => {
    handleUpdateUser();
  }, [handleUpdateUser]);

  return (
    <Modal setIsOpen={setIsOpen} isOpen={isOpen}>
      {children}
      <Form ref={formRef} onSubmit={handleSubmit}>
        <Input placeholder="Nome" name="name" icon={FiUser} />
        <Input type="email" placeholder="E-mail" name="email" icon={FiMail} />
        <Select
          name="Grupo Usuário"
          data={groupUsers}
          icon={AiOutlineUsergroupAdd}
        />
        <Select name="Perfil de Acesso" data={profilesUser} icon={ImProfile} />

        <Button type="submit" buttonType="success">
          Criar Usuario
        </Button>
      </Form>
    </Modal>
  );
};
