import React, { useEffect, useState } from 'react';
import { GrFormPrevious, GrNext } from 'react-icons/gr';
import { TableStyle, Pagination } from './styles';

interface TableProps {
  limitPerPage?: number;
  totalItens?: number;
}

const Table: React.FC<TableProps> = ({
  limitPerPage,
  totalItens,
  children,
}) => {
  const [pageCurrent, setPageCurrent] = useState(1);
  const [pages, setPages] = useState<number[] | never>([]);

  useEffect(() => {
    async function loadProducts() {
      if (totalItens && limitPerPage) {
        const totalPages = Math.ceil(totalItens / limitPerPage);

        const arrayPages = [];
        let i = 1;
        while (i <= totalPages) {
          arrayPages.push(i);
          i += 1;
        }

        setPages(arrayPages);
      }
    }

    loadProducts();
  }, []);

  return (
    <>
      <TableStyle>{children}</TableStyle>
      <Pagination>
        <strong>
          Qtd:
          {totalItens}
        </strong>
        <br />
        <div>
          <button type="button">
            <GrFormPrevious size={25} color="#2691f0" />
          </button>
          {pages.map(page => (
            <button type="button" key={page}>
              {page}
            </button>
          ))}
          <button type="button">
            <GrNext size={25} color="#2691f0" />
          </button>
        </div>
      </Pagination>
    </>
  );
};

export default Table;
