import styled from 'styled-components';

export const TableStyle = styled.table`
  width: 100%;
  border-collapse: collapse;
  thead {
    background: linear-gradient(64.42deg, #35cfff 21.97%, #2691f0 99.92%);
  }
  th {
    color: #fff;
    font-weight: normal;
    padding: 0.8rem 2rem;
    text-align: left;
    font-size: 1.3rem;
    line-height: 1rem;
    font-family: Roboto;

    text-align: center;
  }
  td {
    padding: 0.6rem 2rem;
    border: 0;
    background: #fff;
    font-size: 1rem;
    font-weight: normal;
    color: #969cb3;
  }
  @media (max-width: 800px) {
    td {
      padding: 0.2rem 1.5rem;
    }

    th {
      padding: 0.4rem 1.5rem;
    }
  }
`;
