import React, { useEffect, useState } from 'react';
import { FaSearch } from 'react-icons/fa';
import { MdNavigateNext, MdNavigateBefore } from 'react-icons/md';
import { TableStyle, Pagination, LimitePerPage, TableFilter } from './styles';

interface TableProps {
  limitPerPage?: number;
  totalItens?: number;
  filterPlaceholder?: string;
  handleFilterTable?(): void;
}

const Table: React.FC<TableProps> = ({
  limitPerPage,
  totalItens,
  children,
  filterPlaceholder,
  handleFilterTable,
}) => {
  /// const [pageCurrent, setPageCurrent] = useState(1);
  const [pages, setPages] = useState<number[] | never>([]);

  useEffect(() => {
    if (totalItens && limitPerPage) {
      const totalPages = Math.ceil(totalItens / limitPerPage);

      const arrayPages = [];
      let i = 1;
      while (i <= totalPages) {
        arrayPages.push(i);
        i += 1;
      }

      setPages(arrayPages);
    }
  }, [limitPerPage, totalItens]);

  return (
    <>
      <TableFilter>
        {limitPerPage && (
          <LimitePerPage>
            <option>10</option>
            <option>20</option>
            <option>30</option>
            <option>50</option>
          </LimitePerPage>
        )}
        {filterPlaceholder && handleFilterTable && (
          <div>
            <FaSearch size={20} />
            <input
              type="text"
              name="FilterTable"
              placeholder={filterPlaceholder}
              onKeyUp={handleFilterTable}
            />
          </div>
        )}
      </TableFilter>
      <TableStyle>{children}</TableStyle>
      {limitPerPage && totalItens && (
        <Pagination>
          <strong>
            Qtd:
            {totalItens}
          </strong>
          <br />
          <div>
            <button type="button">
              <MdNavigateBefore size={25} />
            </button>
            {pages.map(page => (
              <button type="button" key={page}>
                {page}
              </button>
            ))}
            <button type="button">
              <MdNavigateNext size={25} />
            </button>
          </div>
        </Pagination>
      )}
    </>
  );
};

export default Table;
