import React, { useState } from 'react';
import { IconBaseProps } from 'react-icons';
import { FaUser } from 'react-icons/fa';
import { RiFileList2Fill, RiFileList3Line } from 'react-icons/ri';
import { Link } from 'react-router-dom';
import { Container, Content, MenuItem, ContainerFloatMenu } from './styles';

interface FloatMenuItemProps {
  icon: React.ComponentType<IconBaseProps>;
  name: string;
  url: string;
}

const FloatMenu: React.FC = ({ children }) => {
  const [isVisible, setIsVisible] = useState(false);
  return (
    <Container>
      <button type="button" onClick={() => setIsVisible(state => !state)}>
        Tarefas
      </button>
      <ContainerFloatMenu>
        <Content>{isVisible && children}</Content>
      </ContainerFloatMenu>
    </Container>
  );
};

export const FloatMenuItem: React.FC<FloatMenuItemProps> = ({
  icon: Icon,
  name,
  url,
}) => {
  return (
    <MenuItem>
      <Icon size={35} />
      <label>{name}</label>
    </MenuItem>
  );
};
export default FloatMenu;
