import React, { useEffect, useMemo, useState } from 'react';
import { array } from 'yup/lib/locale';
import { TableStyle, Pagination } from './styles';

interface TableProps {
  limitPerPage?: number;
  totalItens?: number;
}

const Table: React.FC<TableProps> = ({
  limitPerPage,
  totalItens,
  children,
}) => {
  const [pageCurrent, setPageCurrent] = useState(1);
  const [pages, setPages] = useState<number[] | never>([]);

  const totalPage = useMemo(() => {
    if (limitPerPage && totalItens) {
      return Math.ceil(totalItens / limitPerPage);
    }
    return 0;
  }, [limitPerPage, totalItens]);

  return (
    <>
      <TableStyle>{children}</TableStyle>
      <Pagination>
        <div>Qtd:</div>
        {pages.map(page => (
          <span key={page}>{page}</span>
        ))}
      </Pagination>
    </>
  );
};

export default Table;
