import { shade } from 'polished';
import styled from 'styled-components';

export const Container = styled.div`
  position: absolute;
`;

export const Content = styled.div`
  margin-top: 5rem;
`;
export const Title = styled.h1`
  font-family: Roboto;
  font-style: normal;
  font-weight: bold;
  font-size: 36px;
  line-height: 42px;
  display: flex;
  align-items: center;
  color: #2691f0;

  padding-left: 2rem;
`;

export const Panel = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;

  width: 100vw;
  margin-top: 1rem;
  .coins-donate {
    background: linear-gradient(244.42deg, #ff6948 0.08%, #ff9d00 78.14%);
  }
  .coins-month {
    background: linear-gradient(64.42deg, #5354ff 21.97%, #b75bff 99.92%);
  }
  .coins-balance {
    background: linear-gradient(64.42deg, #00df3e 0%, #42ccd5 99.92%);
  }
  div {
    width: 19rem;
    height: 14rem;
    box-shadow: 0px 4px 13px rgba(130, 196, 255, 0.45);
    border-radius: 25px;

    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;

    padding: 1rem;

    span {
      font-family: Roboto;
      font-style: normal;
      font-weight: bold;
      font-size: 28px;
      line-height: 33px;
      display: flex;
      align-items: center;
      text-align: center;

      color: #ffffff;

      margin-top: 1rem;
    }
    &:hover {
      transform: translateY(1rem);
    }
  }
`;
