import React, { useCallback, useMemo, useRef } from 'react';
import { FiMail, FiUser } from 'react-icons/fi';
import { Form } from '@unform/web';
import { FormHandles } from '@unform/core';
import * as Yup from 'yup';
import { AiOutlineUsergroupAdd } from 'react-icons/ai';
import Button from '../../../../components/Button';
import Container from '../../../../components/Container';
import Input from '../../../../components/Input';
import Select from '../../../../components/Select';
import { Content } from './styles';
import getValidationErrors from '../../../../util/getValidationErrors';
import { useToast } from '../../../../hooks/toast';

interface FormData {
  name: string;
  email: string;
}

const UserRegister: React.FC = () => {
  const formRef = useRef<FormHandles>(null);
  const { addToast } = useToast();
  const groupUsers = useMemo(() => {
    return [
      {
        value: '1',
        description: 'Belo Horizonte',
      },
      {
        value: '2',
        description: 'Distrito Federal',
      },
      {
        value: '1',
        description: 'Brasilia',
      },
    ];
  }, []);
  const handleSubmit = useCallback(
    async (data: FormData) => {
      formRef.current?.setErrors({});
      try {
        const schema = Yup.object().shape({
          name: Yup.string().required('Obrigatório informar o nome.'),
          email: Yup.string().required('Obrigatório informar o e-mail.'),
        });
        await schema.validate(data);
      } catch (err) {
        if (err instanceof Yup.ValidationError) {
          const errors = getValidationErrors(err);
          formRef.current?.setErrors(errors);
          return;
        }
        addToast({
          title: 'Houve um erro ao cadastrar usuário.',
          description:
            'Entre em contato com suporte técnico para verificar a falha.',
          type: 'error',
        });
      }
    },
    [addToast],
  );
  return (
    <Container>
      <Content>
        <Form ref={formRef} onSubmit={handleSubmit}>
          <Input name="name" type="text" placeholder="Nome" icon={FiUser} />
          <Input name="email" type="email" placeholder="E-mail" icon={FiMail} />
          <Select
            name="Grupo Usuário"
            data={groupUsers}
            icon={AiOutlineUsergroupAdd}
          />

          <Button type="submit" buttonType="success">
            Criar Usuario
          </Button>
        </Form>
      </Content>
    </Container>
  );
};

export default UserRegister;
