import { shade } from 'polished';
import styled from 'styled-components';

export const Content = styled.main`
  display: flex;
  justify-content: center;
  align-items: center;

  flex-direction: column;

  form {
    width: 30rem;
    > div:first-child {
      width: calc(50% - 0.6rem);
    }
    margin-bottom: 3rem;
  }

  table {
    border-spacing: 0 8px;
    thead {
      background: linear-gradient(64.42deg, #35cfff 21.97%, #2691f0 99.92%);
    }
    th {
      color: #969cb3;
      font-weight: normal;
      padding: 20px 32px;
      text-align: left;
      font-size: 16px;
      line-height: 24px;
    }
    td {
      padding: 20px 32px;
      border: 0;
      background: #fff;
      font-size: 16px;
      font-weight: normal;
      color: #969cb3;
    }
    td:first-child {
      border-radius: 8px 0 0 8px;
    }

    td:last-child {
      border-radius: 0 8px 8px 0;
    }
  }
`;

export const NewPassword = styled.div`
  display: flex;
  div + div {
    margin: 0 0 0 1rem;
  }
`;

export const ProfileAvatar = styled.div`
  margin: 2rem 0;
  position: relative;
  input {
    display: none;
  }
  img {
    height: 186px;
    width: 186px;
    border-radius: 50%;
  }
  > label {
    position: absolute;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;

    background: #2691f0;

    height: 50px;
    width: 50px;

    right: 0;
    top: 140px;

    border-radius: 50%;
    :hover {
      background: ${shade('0.2', '#2691f0')};
    }
  }
`;
