import React, { useCallback, useState } from 'react';
import { FiEdit } from 'react-icons/fi';
import { HiUserRemove } from 'react-icons/hi';
import { useRouteMatch } from 'react-router-dom';
import Container from '../../../../components/Container/index';
import Table from '../../../../components/Table';
import { ModalEditUser } from './ModalUpdateUser';
import { Content, Title, EditButton, RemoveButton } from './styles';

interface IUserUpdate {
  id: string;
  name: string;
  group_user: string;
  profile_access: string;
  email: string;
}

const UserList: React.FC = () => {
  const [editModalOpen, setEditModalOpen] = useState(false);
  const [idUserUpdate, setIdUserUpdate] = useState('');

  const handleUpdateUser = useCallback(
    (userData: Omit<IUserUpdate, 'id'>) => {
      // capturar o id na variÃ¡vel idUserUpdate e chamar API para atualizar
      const userUpdate = {
        ...userData,
        id: idUserUpdate,
      };
    },
    [idUserUpdate],
  );

  const togleEditModalOpen = useCallback(() => {
    setEditModalOpen(state => !state);
  }, []);

  const handleEditUserModal = useCallback(
    (idUser: string) => {
      togleEditModalOpen();
      setIdUserUpdate(idUser);
    },
    [setIdUserUpdate, togleEditModalOpen],
  );

  const handleDeleteUser = useCallback(() => {
    // sahdfj
  }, []);

  return (
    <>
      <Container>
        <Content>
          <Title>
            <strong> UsuÃ¡rios</strong>
            <p>Listagem de usuÃ¡rios</p>
          </Title>

          <Table limitPerPage={10} totalItens={100}>
            <thead>
              <tr>
                <th>Nome</th>
                <th>E-mail</th>
                <th>Perfil</th>
                <th>Grupo</th>
                <th>Editar</th>
                <th>Excluir</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td className="text-left">Jhonh Doe</td>
                <td className="text-left">jhondoe@example.com.br</td>
                <td className="text-left">Administrador</td>
                <td className="text-left">Belo Horizonte</td>
                <td className="text-center">
                  <EditButton
                    type="button"
                    onClick={() => {
                      handleEditUserModal('787');
                    }}
                  >
                    <FiEdit size={25} />
                  </EditButton>
                </td>
                <td className="text-center">
                  <RemoveButton type="button" onClick={handleDeleteUser}>
                    <HiUserRemove size={27} />
                  </RemoveButton>
                </td>
              </tr>
            </tbody>
          </Table>
        </Content>
      </Container>

      <ModalEditUser
        setIsOpen={togleEditModalOpen}
        isOpen={editModalOpen}
        handleUpdateUser={handleUpdateUser}
      />
    </>
  );
};

export default UserList;
