import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 999999;

  width: 100vw;
  height: 100vh;

  position: absolute;

  top: 0;
  bottom: 0;

  background-color: #ccc;
  opacity: 0.7;
`;
