import styled from 'styled-components';

export const Content = styled.div`
  margin-top: 2rem;
  padding: 1rem;

  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;

  width: 100%;
  max-width: 50rem;
  form {
    padding: 1rem;
    flex: 1;
  }
`;

export const Title = styled.div`
  background-color: #fff;

  height: 4rem;
  border-radius: 0.8rem;

  padding: 0 1rem;
  margin: 1rem 0;
  width: calc(100% - 4rem);

  display: flex;
  flex-direction: column;
  justify-content: center;

  strong {
    font-family: Roboto;
    font-weight: 800;
    font-size: 1.8rem;
    line-height: 2rem;

    color: #2691f0;
  }
  p {
    font-family: Roboto;
    font-weight: 800;
    font-size: 1.1rem;
    line-height: 1.4rem;

    color: #888888;
  }
`;
