import React, { useCallback } from 'react';
import { GiHamburgerMenu } from 'react-icons/gi';
import { FiLogOut } from 'react-icons/fi';
import { Link } from 'react-router-dom';
import { Container, Profile } from './styles';
import { useAlert } from '../../hooks/alert';
import { useMenu } from '../../hooks/menu';
// import { useAuth } from '../../hooks/auth';

const Header: React.FC = () => {
  const { isVisibleMenu, setIsVisibleMenu } = useMenu();
  // const { user } = useAuth();

  const { addAlert } = useAlert();

  // const { signOut } = useAuth();

  const handleSignOut = useCallback(() => {
    addAlert({
      message: 'Deseja realmente sair do sistema?',
      type: 'confirmation',
    });
  }, [addAlert]);

  return (
    <>
      <Container isVisibleMenu={isVisibleMenu}>
        <button type="button" onClick={() => setIsVisibleMenu(!isVisibleMenu)}>
          <GiHamburgerMenu size={25} color="#000" />
        </button>
        <Profile>
          <Link to="/profile" title="Perfil">
            <img
              src="https://avatars2.githubusercontent.com/u/48379411?s=460&u=94dabeb8a22a6181fc4686a5bee1e608fa477f86&v=4"
              alt="Michele de Freitas"
            />
            <strong>Michele de Freitas</strong>
          </Link>
          <button type="button" title="Sair" onClick={handleSignOut}>
            <FiLogOut size={32} />
          </button>
        </Profile>
      </Container>
    </>
  );
};
export default Header;
